sys_script_include.script:
var MyScriptInclude = Class.create();
MyScriptInclude.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	mysimplefunction: function() {
		var result = "my simple function return value";
		return result;
	},

	mycomplicatedfunction: function() {
		var value = null;
		try {
			var result = [];
			var gr = new GlideRecord("x_58872_needit_test");
			gr.query();
			while(gr.next()) {
				var myitem = {text:gr.mytext.toString(), date:gr.mydate.toString()};
				result.push(myitem);
			}
			value = new global.JSON().encode(result);//JSON formatted string
		}
		catch(ex) {
			gs.addErrorMessage("Exception:" + ex.message);
		}
		return value;				
	},

    type: 'MyScriptInclude'
});
